import React, { useContext, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Web3Context from '../contexts/Web3Context';

// Define the sports categories
const sportsCategories = [
  {
    id: 'cricket',
    name: 'Cricket',
    description: 'International matches and premier league games',
    image: 'https://images.unsplash.com/photo-1540747913346-19e32dc3e97e?auto=format&fit=crop&w=400&h=320',
    // icon: 'https://cdn-icons-png.flaticon.com/512/1099/1099680.png',
  },
  {
    id: 'football',
    name: 'Football',
    description: 'Premier League, La Liga, and international tournaments',
    image: 'data:image/jpeg;base64,',
    // icon: 'https://cdn-icons-png.flaticon.com/512/3231/3231063.png',
  },
  {
    id: 'basketball',
    name: 'Basketball',
    description: 'NBA games and international championships',
    image: 'data:image/jpeg;base64,',
    // icon: 'https://cdn-icons-png.flaticon.com/512/1161/1161561.png',
  },
];

const DashboardPage = () => {
  const { isConnected, connectWallet } = useContext(Web3Context);
  const navigate = useNavigate();

  // Redirect to landing page if not connected
  useEffect(() => {
    if (!isConnected) {
      navigate('/');
    }
  }, [isConnected, navigate]);

  const handleConnect = async () => {
    await connectWallet();
  };

  if (!isConnected) {
    return (
      <div className="min-h-screen bg-secondary flex justify-center items-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Please connect your wallet</h2>
          <button onClick={handleConnect} className="btn-primary">
            Connect Wallet
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-secondary">
      <div className="container-custom">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">Available Sports</h1>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {sportsCategories.map((sport) => (
            <Link 
              key={sport.id} 
              to={`/sports/${sport.id}`}
              className="card group overflow-hidden"
            >
              <div className="relative h-48 mb-4 bg-gray-200 rounded-lg overflow-hidden">
                <img
                  src={sport.image}
                  alt={sport.name}
                  className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-black bg-opacity-30 flex items-center justify-center">
                  <h3 className="text-white text-2xl font-bold">{sport.name}</h3>
                </div>
              </div>
              <div>
                <div className="flex items-center gap-2 mb-2">
                  <span className="text-primary">{sport.icon}</span>
                  <h3 className="font-semibold text-lg">{sport.name}</h3>
                </div>
                <p className="text-gray-600">{sport.description}</p>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;